import React, { useState } from 'react';





export const Body = () => {

    // const state = useState();
    // const [count, setCount] = useState(0);
    let count = 0;
    const inC = () => {
        // setCount(count + 1);
        console.log(count++);
    }

    const deC = () => {
        // setCount(count - 1);
        console.log(count--);
    }




    return (
        <>
            <div>
                <h2>{count}</h2>
                <button className="ic-btn" onClick={inC}>+</button>
                <button className="ic-btn" onClick={deC}>-</button>
            </div>
        </>
    )
}

export default Body;
