import React, {useState} from "react";
import { Container, Row, Col, Alert, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import {MdAdd} from "react-icons/md"
import {BsPlusLg} from "react-icons/bs"

export const Practice = () => {

    

    {/* ============================ INCRIMENT DECRIMENT ==================================== */}

        
    const [IncDecValue, setIncDecValue] = useState(0);
    const IncrementButton = () => {
        if(IncDecValue >= 0 && IncDecValue < 10) {        
            setIncDecValue(IncDecValue + 1);
        }
    } 
    const DecrementButton = () => {
        if(IncDecValue > 0) {            
            setIncDecValue(IncDecValue - 1);
        } else {
            alert("It's less then Zerro");
        }
    }

    {/* ============================ picup wishes ==================================== */}
    

    let d = new Date().getHours();
    let wishes = '';

    const chingeheading = {
        color: 'black',
        backgroundColor: 'skyblue',
        padding: '10px',
        margin: '0 auto',
        textAlign: 'center'
    }

    const headingSpan = {
        fontWeight: '400'
    }



    if(d>=1 && d<12){
        wishes = "Good Morning"
        headingSpan.color = '#fff';
    } else if(d>=12 && d<20){
        wishes = "Good Afternoon";
        headingSpan.color= 'rgb(61, 165, 63)';
    } else {
        wishes = "Good Night";
        headingSpan.color = 'red';
    }       
    

    {/* ============================ calculator ==================================== */}
    function add(x,y) {
        let result = x+y;
        return result;
    }
    
    function sub(x,y) {
        let result = x-y;
        return result;
    }
    
    function mul(x,y) {
        let result = x*y;
        return result;
    }
    
    function divi(x,y) {
        let result = x/y;
        result = result.toFixed(3);
        return result;
    }

    {/* ============================ data collection on form ==================================== */}

    const [storeValue, setStore] = useState();      

    const [Heading, setHeading] = useState({        
        Fname: "",
        Lname: "",
        Email: "",
        PhoneNo: "",
    });

    const inputValue = (event) => {
        const {name, value} = event.target; // "that means" 'const value = event.target.value;' and 'const name = event.target.name;'

        setHeading(() => {

            return {
                ...Heading,
                [name] : value,
            }
        });

    }

    const Submit = (event) => {
        setStore(Heading.Fname + ", " + Heading.Lname  + ", " + Heading.Email  + ", " + Heading.PhoneNo);
    }


    {/* ============================ show time ==================================== */}
    let Time = new Date().toLocaleTimeString();
    const [ShowTime, setShowTime] = useState(Time);
    setInterval(() => {
        let CTime = new Date().toLocaleTimeString();
        setShowTime(CTime);
    }, 1000);


    

    {/* ============================ ToDoList ==================================== */}

    const addButton = {
        width: '20px', 
        textAlign: 'center', 
        height: '20px',
        background: '#fff',
        marginRight: '6px',
        color: 'blue',
        fontSize: '18px',
        lineHeight: '19px',
        overflow: 'hidden',
        display: 'inline-block',
        cursor: 'pointer',
    }



    

    // const [TodoOutput, setTodoOutput] = useState();
    // const [TodoListArray, setTodoListArray] = useState([]);

    // const ToDoInput = (event) => {
    //     setTodoOutput(event.target.value);
    // } 

    // const addToDoList = () => {
    //     setTodoListArray((oldArray) => {
    //         return([...oldArray, TodoOutput]);
    //     });
    //     setTodoOutput("");
    // }

    // const Dishmiss = (event) => {
    //     event.preventDefault();
    //     setTodoListArray((oldArray) => {
    //         return((               
    //             TodoListArray
    //         ));
    //     });
    //     console.log("ok");
    // }

    const [InputList, setInputList] = useState();
    const InputChange = (event) => {
        setInputList(event.target.value);
    }

  

    return(
        <>

        {/* ============================ New TODOLIST ==================================== */}

        <Container className="btToDoList bg-black pt-3 pb-80">
            <Row className="justify-content-center">
                <Col sm={7}>
                    <Row className="align-items-center">
                        <Col sm={9}>
                            <input className="mb-0"
                                type="text"
                                placeholder="add"
                                onChange={InputChange}
                            />
                        </Col>
                        <Col sm={3}>
                            <Button variant="success"><BsPlusLg /></Button>{' '}

                        </Col>
                    </Row>
                    <ul className="color-white p-0">
                        <li>{InputList}</li>
                    </ul>
                </Col>
            </Row>
        </Container>

        {/* ============================ show time ==================================== */}
        {/* <p>{ShowTime}</p> */}
        
            {/* ============================ INCRIMENT DECRIMENT ==================================== */}
            {/* <div className="bg-black">
            <p className="color-white">{IncDecValue}</p>
                <div><span onClick={IncrementButton} style={addButton}>+</span><span onClick={DecrementButton} style={addButton}>-</span></div>
            </div> */}
        {/* ============================ picup wishes ==================================== */}
        {/* <h1 style={chingeheading}>Hellow sir, <strong style={headingSpan}>{wishes}</strong></h1> */}

            {/* ============================ calculator ==================================== */}

                {/* <h1 className="ic-mt-30">calculator</h1>
                    <ul>
                        <li>{add(40,20)}</li>
                        <li>{sub(10,20)}</li>
                        <li>{mul(10,20)}</li>
                        <li>{divi(20,15)}</li>
                    </ul> */}


            {/* ============================ data collection on form ==================================== */}
        {/* <h1 className="ic-mt-30">data collection form</h1>
           
           <form className="bg-black">
           <h2 className="color-white">{storeValue}</h2>
                <input 
                    type="text" 
                    placeholder="enter first name" 
                    name="Fname"
                    onChange={inputValue} 
                    value={Heading.Fname}
                />
                <input 
                    type="text" 
                    placeholder="enter last name" 
                    name="Lname"
                    onChange={inputValue} 
                    value={Heading.Lname}
                />
                <input 
                    type="text" 
                    placeholder="Email" 
                    name="Email"
                    onChange={inputValue} 
                    value={Heading.Email}
                />
                <input 
                    type="number" 
                    placeholder="Phone no" 
                    name="PhoneNo"
                    onChange={inputValue} 
                    value={Heading.PhoneNo}
                />

                <Link to="{void(0)}" onClick={Submit}>Submit</Link>
            </form> */}

            {/* ============================ ToDoList ==================================== */}
            {/* <div className="ToDoList-wrap ic-pt-40 ic-pb-40 bg-blue ">
                <h1 className="ic-mt-30 color-white ic-mb-20">ToDo List</h1>
                <form aaction="#" clssName="d-flex gap-2 align-items-center ic-mb-20">
                    <input className="ic-mb-0" 
                    type="text" 
                    placeholder="write list"
                    onChange={ToDoInput}
                    value={TodoOutput}
                     />
                    <Link to="/" 
                    style={addButton}
                    onClick={addToDoList}
                    > <MdAdd /> </Link>
                </form>           

                <ul className="todobody">

                    {
                        TodoListArray.map((ListArray, index) => {
                            return (
                                <div className="d-flex align-item-center color-white" key={index} id={index}>
                                    <Link to="/" style={addButton} onClick={Dishmiss}>X</Link>
                                    <li>{ListArray}</li>
                                </div>
                            )
                        })
                    }

                </ul>
            </div> */}
            
        </>
    )
}


