import React, {useState} from "react";

export const Body = () => {
    const [Heading, setHeading] = useState('Output');
    // const [Fname, setFname] = useState();
    // const [Lname, setLastName] = useState();
    // const [Email, setEmail] = useState();
    // const [PhoneNo, setPhoneNo] = useState();

    const inputValue = (event) => {
        setFname(event.target.value);
    }
    // const LastName = (event) => {
    //     setLastName(event.target.value);
    // }
    // const EmailValue = (e) => {
    //     setEmail(e.target.value);
    // }

    // const PhoneValue = (event) => {
    //     setPhoneNo(event.target.value);
    // }
    const Submit = () => {
        /* setHeading(Fname + " " + Lname + " " + Email + " " + PhoneNo);*/
        setHeading(inputValue);
    }
    return(
        <>
            <form className="bg-black">
                <h2 className="color-white">{Heading}</h2>
                <input type="text" placeholder="enter first name" className="Fname" 
                
                onChange={inputValue} 
                value={Fname} 
                />
                <input type="text" placeholder="enter last name" className="Fname" 
                
                onChange={inputValue} 
                value={Lname} 
                />
                <input type="text" placeholder="Email" className="Fname" 
                
                onChange={inputValue} 
                value={Email} 
                />
                <input type="text" placeholder="Phone no" className="Fname" 
                
                onChange={inputValue} 
                value={PhoneNo} 
                />

                <a href="javascript:void(0)" onClick={Submit}>Submit</a>
            </form>
        </>
    )
}
