import React, {useState} from "react";
import { Link } from "react-router-dom";

export const Practice = () => {
    {/* ============================ picup wishes ==================================== */}
    

    let d = new Date().getHours();
    let wishes = '';

    const chingeheading = {
        color: 'black',
        backgroundColor: 'skyblue',
        padding: '10px',
        margin: '0 auto',
        textAlign: 'center'
    }

    const headingSpan = {
        fontWeight: '400'
    }



    if(d>=1 && d<12){
        wishes = "Good Morning"
        headingSpan.color = '#fff';
    } else if(d>=12 && d<20){
        wishes = "Good Afternoon";
        headingSpan.color= 'rgb(61, 165, 63)';
    } else {
        wishes = "Good Night";
        headingSpan.color = 'red';
    }       
    

    {/* ============================ calculator ==================================== */}
    function add(x,y) {
        let result = x+y;
        return result;
    }
    
    function sub(x,y) {
        let result = x-y;
        return result;
    }
    
    function mul(x,y) {
        let result = x*y;
        return result;
    }
    
    function divi(x,y) {
        let result = x/y;
        result = result.toFixed(3);
        return result;
    }

    {/* ============================ data collection on form ==================================== */}

    const [storeValue, setStore] = useState();      

    const [Heading, setHeading] = useState({        
        Fname: "",
        Lname: "",
        Email: "",
        PhoneNo: "",
    });

    const inputValue = (event) => {
        const {name, value} = event.target; // "that means" 'const value = event.target.value;' and 'const name = event.target.name;'

        setHeading(() => {

            return {
                ...Heading,
                [name] : value,
            }
        });

    }

    const Submit = (event) => {
        setStore(Heading.Fname + ", " + Heading.Lname  + ", " + Heading.Email  + ", " + Heading.PhoneNo);
    }

    {/* ============================ ToDoList ==================================== */}

    const addButton = {
        width: '20px', 
        textAlign: 'center', 
        height: '20px',
        background: '#fff',
        marginRight: '6px',
        color: 'blue',
        fontSize: '18px',
        lineHeight: '19px',
        overflow: 'hidden',
        display: 'inline-block',
        cursor: 'pointer',
    }

    
    // const [TodolistOutput, setTodolistOutput] = useState('');
    // const [TodoOutput, setTodoOutput] = useState([]);

    // const inputTodo = (event) => {
    //     setTodolistOutput(event.target.value);
    // }


    // const addToDoList = () => {
    //     setTodoOutput((todoArray) => {
    //         return([...todoArray, TodolistOutput]);
    //     });

    //     setTodolistOutput("");
    // };


    const addToDoList = () => {
        console.log('ssssssssss');
    }


    return(
        <>
        {/* ============================ ToDoList ==================================== */}
        <div className="ToDoList-wrap ic-pt-40 ic-pb-40 bg-blue ">
                <h1 className="ic-mt-30 color-white ic-mb-20">ToDo List</h1>
                <form action="#" className="d-flex gap-2 align-items-center ic-mb-20">
                    <input className="ic-mb-0" 
                    type="text" 
                    placeholder="write list"
                    onChange={ToDoInput}
                     />
                    <Link to="/" 
                    style={addButton}
                    onClick={addToDoList}
                    > + </Link>
                </form>           

                <ul className="todobody">
                    {/* <li className="color-white d-flex gap-2 align-items-center ic-mb-20">
                        <Link to="/" className="dishmish" style={addButton}>X</Link>
                        {TodoOutput}
                    </li> */}

                </ul>
            </div>

            
            {/* ============================ picup wishes ==================================== */}
            <h1 style={chingeheading}>Hellow sir, <strong style={headingSpan}>{wishes}</strong></h1>

            {/* ============================ calculator ==================================== */}

                <h1 className="ic-mt-30">calculator</h1>
                    <ul>
                        <li>{add(40,20)}</li>
                        <li>{sub(10,20)}</li>
                        <li>{mul(10,20)}</li>
                        <li>{divi(20,15)}</li>
                    </ul>


            {/* ============================ data collection on form ==================================== */}
        <h1 className="ic-mt-30">data collection form</h1>
           
           <form className="bg-black">
           <h2 className="color-white">{storeValue}</h2>
                <input 
                    type="text" 
                    placeholder="enter first name" 
                    name="Fname"
                    onChange={inputValue} 
                    value={Heading.Fname}
                />
                <input 
                    type="text" 
                    placeholder="enter last name" 
                    name="Lname"
                    onChange={inputValue} 
                    value={Heading.Lname}
                />
                <input 
                    type="text" 
                    placeholder="Email" 
                    name="Email"
                    onChange={inputValue} 
                    value={Heading.Email}
                />
                <input 
                    type="number" 
                    placeholder="Phone no" 
                    name="PhoneNo"
                    onChange={inputValue} 
                    value={Heading.PhoneNo}
                />

                <Link to="{void(0)}" onClick={Submit}>Submit</Link>
            </form>

            
            
        </>
    )
}


