// import React, {useState} from "react"; || NOT MENDATORY
import {useState} from "react";
import { Container, Row, Col, Alert, Button } from "react-bootstrap";
import {MdAdd, MdMinimize, MdDeleteForever} from "react-icons/md"
import {BsPlusLg} from "react-icons/bs";


export const ToDOList = () => {

  // const [name, setName] = useState('');
  // const [id, setId] = useState(0);
  // const [items, setItems] = useState([]);

  // const addHandler = () => {
  //   let data = {
  //     name: name,
  //     id: id,
  //   };
  //   setItems([...items, data]);
  //   setId(id + 1);
  // };

  // const deleteHandler = (pId) => {
  //   let updateArr = items.filter((x) => x.id !== pId);
  //   setItems(updateArr);
  // };
  // return (
  //   <div>
  //     <input type="text" onChange={(e) => setName(e.target.value)} />
  //     <button onClick={addHandler}>click</button>
  //     <hr />
  //     <ul>
  //       {items.map((i) => (
  //         <li key={i.id}>
  //           <span>{i.name}</span>
  //           <button onClick={() => deleteHandler(i.id)}>delete</button>
  //         </li>
  //       ))}
  //     </ul>
  //   </div>
  // );

   
    const [item, setItem] = useState('')
    const [id, setid] = useState(0)
    const[ListArray, setListArray] = useState([]);

    const addList = () => {
      let data = {
          item: item,
          id: id
      };
      setListArray([...ListArray, data]);
      setid(id + 1)
      setItem('')
    }

    const DeleteItem = (Pid) => {
        let updateArr = ListArray.filter(x => x !== Pid)
       setListArray(updateArr)
    }
    
    return(
        <>
            <Container className="btToDoList bg-black pt-3 pb-4">
                <Row className="justify-content-center">
                    <Col sm={7}>
                        <Row className="align-items-center">
                            <Col sm={9}>
                                <input className="mb-0" type="text" value={item} placeholder="add a item" onChange = {(e) => setItem(e.target.value)}/>
                            </Col>
                            <Col sm={3}>
                                <Button variant="success" onClick={addList}><BsPlusLg /></Button>
                            </Col>
                        </Row>
                        <ul className="color-white p-0">
                            {
                                ListArray.map((val) => {
                                    return (
                                        <div key={val.id} style={{display: val ? "flex" : "none"}}>
                                            <span onClick={() => DeleteItem(val.id)}><MdDeleteForever/></span>
                                            <li>{val.item}</li>
                                        </div>
                                    )
                                })
                            }
                        </ul>
                    </Col>
                </Row>
            </Container>           
        </>
    )
}
