// import React, {useState} from "react"; || NOT MENDATORY
import {useState} from "react";
import { Container, Row, Col, Alert, Button } from "react-bootstrap";
import {MdAdd, MdMinimize, MdDeleteForever} from "react-icons/md"
import {BsPlusLg} from "react-icons/bs";


export const ToDOList = () => {

    // const [todoList, settodoList] = useState();
    const [item, setItem] = useState('')
    const[ListArray, setListArray] = useState([]);

    const addList = () => {
        setListArray((oldArray) => {
            return[...oldArray, item]
        });
        setItem('')
    }

    const DeleteItem = (val) => {
        // console.log(val)
        let updateArr = ListArray.filter(x => x !== val)
        // console.log('up ',updateArr)
       setListArray(updateArr)
    }

console.log("array: ",ListArray)
    
    return(
        <>
            <Container className="btToDoList bg-black pt-3 pb-4">
                <Row className="justify-content-center">
                    <Col sm={7}>
                        <Row className="align-items-center">
                            <Col sm={9}>
                                <input className="mb-0"
                                    type="text"
                                    value={item}    
                                    placeholder="add a item"
                                    onChange = {e => setItem(e.target.value)}
                                />
                            </Col>
                            <Col sm={3}>
                                <Button variant="success" onClick={addList}><BsPlusLg /></Button>
                            </Col>
                        </Row>
                        <ul className="color-white p-0">
                            {
                                ListArray.map((val, index) => {
                                    return (
                                        <div key={index} id={index} style={{display: val ? "flex" : "none"}}>
                                            <span onClick={() => DeleteItem(val)}><MdDeleteForever/></span>
                                            <li>{val}</li>
                                        </div>
                                    )
                                })
                            }
                        </ul>
                    </Col>
                </Row>
            </Container>           
        </>
    )
}
