import React, {useState} from "react";
import { Link } from "react-router-dom";

export const Body = () => {

    {/* ============================ calculator ==================================== */}
    function add(x,y) {
        let result = x+y;
        return result;
    }
    
    function sub(x,y) {
        let result = x-y;
        return result;
    }
    
    function mul(x,y) {
        let result = x*y;
        return result;
    }
    
    function divi(x,y) {
        let result = x/y;
        result = result.toFixed(3);
        return result;
    }

    {/* ============================ data collection on form ==================================== */}

    const [storeValue, setStore] = useState();      

    const [Heading, setHeading] = useState({        
        Fname: "",
        Lname: "",
        Email: "",
        PhoneNo: "",
    });

    const inputValue = (event) => {
        const {name, value} = event.target; // "that means" 'const value = event.target.value;' and 'const name = event.target.name;'

        setHeading(() => {

            return {
                ...Heading,
                [name] : value,
            }
        });

    }

    const Submit = (event) => {
        setStore(Heading.Fname + ", " + Heading.Lname  + ", " + Heading.Email  + ", " + Heading.PhoneNo);
    }

    {/* ============================ data collection on form ==================================== */}


    

    return(
        <>

            {/* ============================ calculator ==================================== */}

                <h1 className="ic-mb-30">collection</h1>
                    <ul>
                        <li>{add(10,20)}</li>
                        <li>{sub(10,20)}</li>
                        <li>{mul(10,20)}</li>
                        <li>{divi(20,15)}</li>
                    </ul>


            {/* ============================ data collection on form ==================================== */}
        <h1 className="ic-mb-30">data collection form</h1>
           
           <form className="bg-black">
           <h2 className="color-white">{storeValue}</h2>
                <input 
                    type="text" 
                    placeholder="enter first name" 
                    name="Fname"
                    onChange={inputValue} 
                    value={Heading.Fname}
                />
                <input 
                    type="text" 
                    placeholder="enter last name" 
                    name="Lname"
                    onChange={inputValue} 
                    value={Heading.Lname}
                />
                <input 
                    type="text" 
                    placeholder="Email" 
                    name="Email"
                    onChange={inputValue} 
                    value={Heading.Email}
                />
                <input 
                    type="number" 
                    placeholder="Phone no" 
                    name="PhoneNo"
                    onChange={inputValue} 
                    value={Heading.PhoneNo}
                />

                <Link to="{void(0)}" onClick={Submit}>Submit</Link>
            </form>

            {/* ============================ data collection on form ==================================== */}
            
        </>
    )
}


