import React, {useState} from "react";
import { Container, Row, Col, Alert, Button } from "react-bootstrap";
import {MdAdd, MdMinimize, MdDeleteForever} from "react-icons/md"
import {BsPlusLg} from "react-icons/bs";


export const ToDOList = () => {

    const [todoList, settodoList] = useState();
    const[ListArray, setListArray] = useState([]);

    const Inchange = (event) => {
        settodoList(event.target.value);
    };

    const addList = () => {
        setListArray((oldArray) => {
            return[...oldArray, todoList]
        });
        settodoList("");
    }


    
    return(
        <>
            <Container className="btToDoList bg-black pt-3 pb-4">
                <Row className="justify-content-center">
                    <Col sm={7}>
                        <Row className="align-items-center">
                            <Col sm={9}>
                                <input className="mb-0"
                                    type="text"
                                    value={todoList}    
                                    placeholder="add a item"
                                    onChange={Inchange}
                                />
                            </Col>
                            <Col sm={3}>
                                <Button variant="success" onClick={addList}><BsPlusLg /></Button>
                            </Col>
                        </Row>
                        <ul className="color-white p-0">
                            {
                                ListArray.map((val) => {
                                    return (
                                        <div style={{display: val ? "flex" : "none"}}>
                                            <span onClick={DeleteItem}><MdDeleteForever/></span>
                                            <li>{val}</li>
                                        </div>
                                    )
                                })
                            }
                        </ul>
                    </Col>
                </Row>
            </Container>           
        </>
    )
}
