import React, {useState} from "react";
// import { Container, Row, Col, Alert, Button } from "react-bootstrap";
// import {MdAdd, MdMinimize, MdDeleteForever} from "react-icons/md"

import {AccordionAPI} from "./AccordionAPI"

const AcHeading = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
}

export const Practice = () => {

    const [show, setshow] = useState("false");

    return(
        <>
        {AccordionAPI.map((val, index) => (
            <div key={index} onClick={(index) => setshow(!show)}>
                <h3 style={AcHeading}>{val.heading}<span>{!show ? "-" : "+"}</span></h3>
                {!show && <p>{val.text}</p>}
                console.log({index});
            </div>  
        ))}
        </>
     )
}
