// import React, {useState} from "react"; || NOT MENDATORY
import {useState} from "react";
import { Container, Row, Col, Alert, Button } from "react-bootstrap";
import {MdAdd, MdMinimize, MdDeleteForever} from "react-icons/md"
import {BsPlusLg} from "react-icons/bs";


const DFlex = {
  display: 'flex',
  gap: '10px',
}


const DNone = {
  display: 'none',
}


export const ToDOList = () => { 
  
  

  // const [item, setItem] = useState('')
  // const [id, setid] = useState(0)
  // const[ListArray, setListArray] = useState([]);
  // const addList = () => {
  //   let data = {
  //     item: item,
  //     id: id,
  //   };
  //   setListArray([...ListArray, data]);
  //   setid(id + 1);
  //   setItem('')
  // };
  // const DeleteItem = (Pid) => {
  //   let updateArr = ListArray.filter((x) => x.id !== Pid)
  //   setListArray(updateArr);
  // };
  //   return(
  //   <>
  //     <Container className="btToDoList bg-black pt-3 pb-4">
  //       <Row className="justify-content-center"><Col sm={7}>
  //         <Row className="align-items-center"><Col sm={9}>
  //           <input className="mb-0" type="text" value={item} placeholder="add a item" onChange = {(e) => setItem(e.target.value)}/></Col>
  //           <Col sm={3}><Button variant="success" onClick={addList}><BsPlusLg /></Button></Col></Row>
  //           <ul className="color-white p-0">
  //             {ListArray.map((val) => {
  //               return (
  //                 <div key={val.id} style={{display: val ? "flex" : "none"}}>
  //                   <span onClick={() => DeleteItem(val.id)}><MdDeleteForever/></span>
  //                   <li>{val.item}</li>
  //                 </div>
  //               )
  //             })}
  //           </ul>
  //       </Col></Row>
  //     </Container>           
  //   </>
  // )


  const [onChange, setonChange] = useState('');
  const [id, setid] = useState(0);
  const [listArray, setlistArray] = useState([]);
  const addButton = () => {
    setlistArray([...listArray, onChange]);
    setid(id + 1);
    setonChange('');
  }
  const DeleteFor = (v) => {
    let UpdateArray = listArray.filter[(iv) => iv.id !== v ];
    setlistArray(UpdateArray);
  }
  
  return(
  <>
    <Container className="btToDoList bg-black pt-3 pb-4">
      <Row className="justify-content-center"><Col sm={7}>
        <Row className="align-items-center"><Col sm={9}>
          <input className="mb-0" type="text" placeholder="add a item" value={onChange} onChange={(e) => setonChange(e.target.value)}/></Col>
          <Col sm={3}><Button variant="success" onClick={addButton}><BsPlusLg /></Button></Col></Row>
          <ul className="color-white p-0">

          {listArray.map((v) => {
            return (
              <div style={v ? DFlex : DNone} key={v.id}><span onClick={(v) => DeleteFor}><MdDeleteForever/></span><li>{v}</li></div>
              )
          })}
          </ul>
      </Col></Row>
    </Container>           
  </>
  )
}
