import React, {useState} from "react";

export const Body = () => {
    const [Heading, setHeading] = useState('');
    const [Fname, setFname] = useState();
    const [Lname, setLastName] = useState();
    const [Email, setEmail] = useState();
    const [PhoneNo, setPhoneNo] = use

    const FirstName = (event) => {
        setFname(event.target.value);
    }
    const LastName = (event) => {
        setLastName(event.target.value);
    }
    const EmailValue = (e) => {
        setEmail(e.target.value);
    }
    const Submit = () => {
        setHeading(Fname + " " + Lname + " " + Email);
    }
    return(
        <>
            <form className="bg-black">
                <h2 className="color-white">This is {Heading}</h2>
                <input type="text" placeholder="enter fname" className="Fname" onChange={FirstName} value={Fname} />
                <input type="text" placeholder="enter fname" className="Fname" onChange={LastName} value={Lname} />
                <input type="text" placeholder="enter fname" className="Fname" onChange={EmailValue} value={Email} />
                <input type="text" placeholder="enter fname" className="Fname" onChange={PhoneValue} value={PhoneNo} />

                <a href="javascript:void()" onClick={Submit}>Submit</a>
            </form>
        </>
    )
}
