import React, {useState} from "react";

export const Body = () => {
    const [storeValue, setStore] = useState();      

    const [Heading, setHeading] = useState({        
        Fname: "",
        Lname: "",
        Email: "",
        PhoneNo: "",
    });

    const inputValue = (event) => {
        const value = event.target.value;
        const name = event.target.name;
        setHeading((preValue) => {
            if(name === 'Fname') {
                return{
                    Fname: value,
                    Lname: preValue.Lname,
                    Email: preValue.Email,
                    PhoneNo: preValue.PhoneNo,  
                }  
            } else if(name === 'Lname') {
                return{
                    Fname: preValue.Fname,
                    Lname: value,
                    Email: preValue.Email,
                    PhoneNo: preValue.PhoneNo,  
                }  
            } else if(name === 'Email') {
                return{
                    Fname: preValue.Fname,
                    Lname: preValue.Lname,
                    Email: value,
                    PhoneNo: preValue.PhoneNo,  
                }  
            } if(name === 'PhoneNo') {
                return{
                    Fname: preValue.Fname,
                    Lname: preValue.Lname,
                    Email: preValue.Email,
                    PhoneNo: value,  
                }  
            }
        });

    }

    const Submit = (event) => {
        event.preventDefault();
        console.log(Heading);
        setStore(Heading.Fname + ", " + Heading.Lname  + " " + Heading.Email  + " " + Heading.PhoneNo);
    }

    

    return(
        <>
            <form className="bg-black">
                <h2 className="color-white">{storeValue}</h2>
                <input type="text" placeholder="enter first name" 
                name="Fname"
                onChange={inputValue} 
                value={Heading.Fname}
                />
                <input type="text" placeholder="enter last name" 
                name="Lname"
                onChange={inputValue} 
                value={Heading.Lname}
                />
                <input type="text" placeholder="Email" 
                name="Email"
                onChange={inputValue} 
                value={Heading.Email}
                />
                <input type="text" placeholder="Phone no" 
                name="PhoneNo"
                onChange={inputValue} 
                value={Heading.PhoneNo}
                />

                <a href="javascript:void(0)" onClick={Submit}>Submit</a>
            </form>
        </>
    )
}
